mapper.select:
select S_ID id, S_NAME name, S_GENDER gender, S_AGE age, S_MAJOR major 
from STUDENT
where S_ID = #{id}

mapper.update:
update STUDENT 
set S_NAME = #{name}, S_GENDER = #{gender}, S_AGE = #{age}, S_MAJOR = #{major} 
where S_ID = #{id}

mapper.select:
select S_ID id, S_NAME name, S_GENDER gender, S_AGE age, S_MAJOR major 
from STUDENT 
where S_GENDER = #{gender} and S_MAJOR = #{major}


